    date_scheduled = fields.Date(string='Fecha de ejecución del pago', copy=True,)
    date_generated = fields.Date(string='Fecha del fichero generado',  copy=False)
    date_uploaded = fields.Date(string='Fecha de subida del fichero',  copy=False)
    date_done = fields.Date(string='Fecha de realización',  copy=False)
    time_of_process = fields.Datetime(string='Fecha Transmisión',  copy=False)
    cheque = fields.Char(string='Cheque', copy=True,)
    narration = fields.Text(string='Notas', copy=True)
    bank_line_count = fields.Integer(string='Número de líneas bancarias', )
