    def name_get(self):
        """
        Si viene el contexto 'employee_show_identification',
        muestra: [identificación] Nombre
        """
        res = []
        show_id = self.env.context.get('employee_show_identification', False)
        for rec in self:
            display = rec.name or ''
            if show_id and rec.identification_id:
                display = "[%s] %s" % (rec.identification_id, display)
            res.append((rec.id, display))
        return res

    @api.model
    def name_search(self, name='', args=None, operator='ilike', limit=100):
        """
        Permite buscar por cédula o por nombre (opt-in por contexto).
        Por defecto lo dejo activado; si quieres, puedes controlarlo con contexto.
        """
        args = args or []
        domain = []
        if name:
            # Si no quieres que aplique en todo lado, pon el default en False y actívalo por contexto.
            use_id_search = self.env.context.get('employee_name_search_with_identification', True)
            if use_id_search:
                domain = ['|', ('identification_id', operator, name), ('name', operator, name)]
            else:
                domain = [('name', operator, name)]
        recs = self.search(domain + args, limit=limit)
        return recs.name_get()


